<script src="{{asset('/vendor/laravel-filemanager/js/stand-alone-button.js')}}"></script>

<script>

    var token = $("meta[name='csrf-token']").attr("content");

    $('#lfm-logo').filemanager('image');
    $('#lfm-background_s1').filemanager('image');
    $('#lfm-background_s2').filemanager('image');
    $('#lfm-background_s3').filemanager('image');
    $('#lfm-background_s4').filemanager('image');

    $("#btnSimpan").click(function(){
        // Buat variabel data untuk menampung data hasil input dari form
        var data = new FormData();

        var url = "{{ route('mbpreference.updatemain') }}";

        data.append('logo', $("#logo").val());
        data.append('nama_aplikasi', $("#nama_aplikasi").val());
        data.append('vidio', $("#vidio").val());
        // data.append('otoritas', $("#otoritas option:selected").val());
        data.append('_token', token);

        if ($("#logo").val()!="" && $("#nama_aplikasi").val()!="" && $("#vidio").val()!=""){
            $.ajax({
                type: 'POST',
                url: url,
                data: data,
                processData: false,
                contentType: false,
                dataType: 'json',
                headers: {
                    'X-CSRF-TOKEN': token // Tambahkan header CSRF
                },
                beforeSend: function(e) {
                    if (e && e.overrideMimeType) {
                        e.overrideMimeType("application/json;charset=UTF-8");
                    }
                },
                success: function(e) {
                    if (e['status'] == true) {
                        showToast('success', e['pesan']);
                    } else {
                        showToast('error', e['pesan']);
                    }
                },
                error: function(xhr, status, error) {
                    // Tangkap error jika terjadi
                    showToast('error', "Terjadi kesalahan");
                    // console.error(xhr.responseJSON);
                }
            });
        }
    });

    $("#btnSimpanBG").click(function(){
        // Buat variabel data untuk menampung data hasil input dari form
        var data = new FormData();

        var url = "{{ route('mbpreference.updatebg') }}";

        data.append('background_s1', $("#background_s1").val());
        data.append('background_s2', $("#background_s2").val());
        data.append('background_s3', $("#background_s3").val());
        data.append('background_s4', $("#background_s4").val());
        // data.append('otoritas', $("#otoritas option:selected").val());
        data.append('_token', token);

        if ($("#background_s1").val()!="" && $("#background_s2").val()!="" && $("#background_s3").val()!="" && $("#background_s4").val()!=""){
            $.ajax({
                type: 'POST',
                url: url,
                data: data,
                processData: false,
                contentType: false,
                dataType: 'json',
                headers: {
                    'X-CSRF-TOKEN': token // Tambahkan header CSRF
                },
                beforeSend: function(e) {
                    if (e && e.overrideMimeType) {
                        e.overrideMimeType("application/json;charset=UTF-8");
                    }
                },
                success: function(e) {
                    if (e['status'] == true) {
                        showToast('success', e['pesan']);
                    } else {
                        showToast('error', e['pesan']);
                    }
                },
                error: function(xhr, status, error) {
                    // Tangkap error jika terjadi
                    showToast('error', "Terjadi kesalahan");
                    // console.error(xhr.responseJSON);
                }
            });
        }
    });

    document.addEventListener('DOMContentLoaded', function () {
